package com.ucap.cloud_web.service.impl;import java.util.List;import java.util.Map;import com.ucap.cloud_web.entity.TimedTaskLog;import com.ucap.cloud_web.service.ITimedTaskLogService;import com.ucap.cloud_web.dao.TimedTaskLogDao;import org.springframework.stereotype.Service;import com.publics.util.page.PageVo;import com.publics.util.utils.QueryUtils;import com.ucap.cloud_web.dto.TimedTaskLogRequest;import org.springframework.beans.factory.annotation.Autowired;/*** <br>* <b>作者：</b>SunJiang<br>* <b>日期：</b> 2015-10-30 09:51:22 <br>* <b>版权所有：<b>版权所有(C) 2015<br>*/@Servicepublic class TimedTaskLogServiceImpl implements ITimedTaskLogService {	@Autowired	private TimedTaskLogDao timedTaskLogDao;	@Override	public void add(TimedTaskLog timedTaskLog){		timedTaskLogDao.add(timedTaskLog);	}	@Override	public TimedTaskLog get(Integer id){		return timedTaskLogDao.get(id);	}	@Override	public void update(TimedTaskLog timedTaskLog){		timedTaskLogDao.update(timedTaskLog);	}	@Override	public void delete(Integer id){		timedTaskLogDao.delete(id);	}	@Override	public PageVo<TimedTaskLog> query(TimedTaskLogRequest request) {		List<TimedTaskLog> timedTaskLog = queryList(request);		PageVo<TimedTaskLog> pv = new PageVo<TimedTaskLog>();		int count = queryCount(request);		pv.setData(timedTaskLog);		pv.setPageNo(request.getPageNo());		pv.setPageSize(request.getPageSize());		pv.setRecordSize(count);		return pv;	}	@Override	public int queryCount(TimedTaskLogRequest request) {		Map<String, Object> param = QueryUtils.getQueryMap(request);		return timedTaskLogDao.queryCount(param);	}	@Override	public List<TimedTaskLog> queryList(TimedTaskLogRequest request) {		Map<String, Object> param = QueryUtils.getQueryMap(request);		List<TimedTaskLog> list = timedTaskLogDao.query(param);		return list;	}}