mapper.update:
update big_data_trend
id = #{id}, site_code = #{siteCode}, scan_date = #{scanDate}, healthindex = #{healthindex}, healthindex_trend = #{healthindexTrend}, linkerrsiteprop = #{linkerrsiteprop}, linkerrsiteprop_trend = #{linkerrsitepropTrend}, updatenum = #{updatenum}, updatenum_trend = #{updatenumTrend}, create_time = #{createTime}, modify_time = #{modifyTime},
where id=#{id}

mapper.select:
select * from big_data_trend


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

select d.name,b.* from 
database_org_info d join database_tree_info t on d.site_code = t.site_code 
join big_org_trend b on t.site_code = b.site_code


	t.parent_id = #{id} 


	and	
	b.scan_date = #{scanDate} 

	and LENGTH(d.site_code) = 6  and t.is_bigdata='1'

	order by CONVERT(${sqlName},DECIMAL) desc


	limit #{start}, #{pageSize}

select d.name,j.* from database_tree_info t join database_tree_info tt on tt.parent_id = t.id join big_data_trend j on j.site_code = tt.site_code join database_info d on d.site_code = tt.site_code
j.scan_date = #{scanDate}
and t.parent_id = #{id}
and d.isorganizational = 1 and t.is_bigdata='1' and tt.is_bigdata='1'
order by CONVERT(${sqlName},DECIMAL) desc
limit #{start}, #{pageSize}

select d.name,b.* from 
database_info d  join database_tree_info t on d.site_code = t.site_code 
join big_data_trend b on t.site_code = b.site_code


		b.scan_date = #{scanDate} 


		and
		t.parent_id = #{id} 

	and LENGTH(d.site_code) = 10 and t.is_bigdata=1

		order by CONVERT(${sqlName},DECIMAL) desc


		limit #{start}, #{pageSize}
