sql.where:
1=1

	and site_code=#{siteCode}


	and question_code like '%${questionCode}%'


	and scan_date=#{scanDate}


	and state=#{state}


	and (scan_date BETWEEN #{startDate} AND #{endDate})

and del_flag=0

mapper.update:
update connection_business_detail
id = #{id}, connection_all_id = #{connectionAllId}, site_code = #{siteCode}, scan_date = #{scanDate}, system_name = #{systemName}, url = #{url}, scan_time = #{scanTime}, state = #{state}, question_describe = #{questionDescribe}, question_code = #{questionCode}, create_time = #{createTime}, del_flag=#{delFlag}
where id=#{id}

mapper.select:
select * from connection_business_detail


	group by ${groupBy}


	order by

		${queryOrder}



	limit #{start}, #{pageSize}


	limit #{begin}, #{pageSize}

select.if:
select count(*)	from (select count(*) from connection_business_detail

	group by ${groupBy}
	)a

mapper.select:
SELECT a.system_name AS systemName, a.question_code AS questionCode, a.scan_time AS scanTime2, a.url as url FROM (SELECT * FROM connection_business_detail cbd WHERE 1 = 1
and cbd.site_code=#{siteCode}
and cbd.scan_date=#{scanDate}
ORDER BY cbd.scan_time DESC ) AS a
WHERE 1 = 1
and a.site_code=#{siteCode}
and a.scan_date=#{scanDate}
GROUP BY a.url ORDER BY a.scan_time DESC
limit #{begin},#{end}

SELECT COUNT(*) FROM ( SELECT a.system_name AS systemName, a.question_code AS questionCode, a.scan_time AS scanTime2 FROM (SELECT * FROM connection_business_detail cbd WHERE 1 = 1
and cbd.site_code=#{siteCode}
and cbd.scan_date=#{scanDate}
ORDER BY cbd.scan_time DESC ) AS a
WHERE 1 = 1
and a.site_code=#{siteCode}
and a.scan_date=#{scanDate}
GROUP BY a.url ORDER BY a.scan_time DESC ) AS b
