id = #{id}, connection_all_id = #{connectionAllId}, site_code = #{siteCode}, scan_date = #{scanDate}, channel_name = #{channelName}, url = #{url}, scan_time = #{scanTime}, state = #{state}, question_describe = #{questionDescribe}, question_code = #{questionCode}, create_time = #{createTime},
where id=#{id}

