SELECT 
  a.channel_name as channelName,
  a.question_code as questionCode,
  a.scan_time AS scanTime2,
  a.url as url 
FROM
  (SELECT 
    * 
  FROM
    connection_channel_detail ccd 
  WHERE 1=1

		and ccd.site_code=#{siteCode}


		and ccd.scan_date=#{scanDate}


  ORDER BY scan_time DESC) AS a 
WHERE 1=1

	and a.site_code=#{siteCode}


	and a.scan_date=#{scanDate}


	GROUP BY ${groupBy}

ORDER BY a.scan_time DESC 

	limit #{begin},#{end}

SELECT COUNT(*) FROM (
	SELECT 
	  a.channel_name as channelName,
	  a.question_code as questionCode,
	  a.scan_time AS scanTime2 
	FROM
	  (SELECT 
	    * 
	  FROM
	    connection_channel_detail ccd 
	  WHERE 1=1

			and ccd.site_code=#{siteCode}


			and ccd.scan_date=#{scanDate}


	  ORDER BY scan_time DESC) AS a 
	WHERE 1=1

		and a.site_code=#{siteCode}


		and a.scan_date=#{scanDate}


		GROUP BY ${groupBy}

	ORDER BY a.scan_time DESC 
) AS b
