sql.where:
1=1

	and site_code=#{siteCode}


	and scan_date=#{scanDate}


	and state=#{state}


	and (scan_date BETWEEN #{startDate} AND #{endDate})

mapper.update:
update connection_home_detail
id = #{id}, connection_all_id = #{connectionAllId}, site_code = #{siteCode}, scan_date = #{scanDate}, state = #{state}, question_describe = #{questionDescribe}, question_code = #{questionCode}, scan_time = #{scanTime}, create_time = #{createTime},
where id=#{id}

mapper.select:
select * from connection_home_detail


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

SELECT 
  di.site_code AS siteCode,
  di.name AS siteName,
  chd.scan_time AS scanTime,
  mt.linkerrprop7 AS linkerrprop7
FROM
  database_tree_info dti,
  connection_home_detail chd,
  database_info di,
  m_taskdetail mt
WHERE dti.site_code = di.site_code 
  AND di.site_code = chd.site_code 
  AND di.site_code = mt.sitecode


  	AND dti.org_site_code =#{orgSiteCode}


  	 AND dti.layer_type =#{layerType}


  	 AND dti.is_link =#{isLink}


  	  AND di.isexp =#{isexp}


  	  AND di.is_scan =#{isScan}


  	  AND chd.scan_date =#{scanDate}


  	  AND mt.countday=#{countday}

GROUP BY chd.site_code 
ORDER BY chd.scan_time DESC
