sql.where:
	1=1


	 and correct_type in (${correctTypeIn})


 	 AND scan_date BETWEEN #{beginScanDate} AND #{endScanDate}


		and site_code=#{siteCode}


		and service_period_id=#{servicePeriodId}


		and correct_type=#{correctType}


		and wrong_term like '%${keyWord}%'


		and scan_date =#{scanDate}


		and scan_date between #{beginTime} and #{endTime}


		and exposure = #{exposure}


		and correct_type!=#{notType}


		and correct_type in 

			#{item}



		and site_code in

			#{item.siteCode}



		and site_code in

			#{item.siteCode}


	and del_flag=0

mapper.update:
update correct_content_detail
id = #{id}, correct_content_info = #{correctContentInfo}, site_code = #{siteCode}, scan_date = #{scanDate}, correct_type = #{correctType}, problem_desc = #{problemDesc}, url = #{url}, img_url = #{imgUrl}, wrong_term = #{wrongTerm}, expect_terms = #{expectTerms}, create_time = #{createTime}, operator = #{operator}, del_flag = #{delFlag}
where id=#{id}

mapper.select:
select * from correct_content_detail

and correct_type!=2
and exposure=1

	order by

		${queryOrder}



	limit #{start}, #{pageSize}

SELECT COUNT(ccd.id) AS wrongNum, ccd.site_code AS siteCode, wi.name AS siteName, wi.url AS homePageUrl, wi.jump_url AS jumpPageUrl, wi.isorganizational AS isorganizational, wi.director AS director FROM correct_content_detail ccd, database_info wi WHERE wi.site_code =ccd.site_code
AND ccd.exposure = 1 and ccd.del_flag=0
AND ccd.scan_date =#{scanDate}
AND ccd.site_code IN
#{item.siteCode}
GROUP BY ccd.site_code ORDER BY wrongNum DESC

SELECT a.site_code, a. NAME AS siteName, a.url AS homePageUrl, a.jump_url AS jumpPageUrl, COUNT(cd.id) AS wrongNum FROM database_info a, correct_content_detail cd, database_tree_info dti WHERE cd.site_code = dti.site_code and a.site_code = dti.site_code
and dti.org_site_code= #{orgSiteCode}
and dti.is_link = #{isLink}
and dti.layer_type = #{siteType}
and cd.correct_type = #{correctType}
AND (cd.scan_date between #{startDate} and #{endDate})
and a.isexp = #{isExp}
and cd.scan_date=#{scanDate}
AND cd.exposure = 1 AND cd.del_flag = 0 GROUP BY cd.site_code ORDER BY wrongNum DESC
limit #{begin},#{end}

SELECT COUNT(*) FROM ( SELECT a.site_code, a. NAME AS siteName, a.url AS homePageUrl, a.jump_url AS jumpPageUrl, COUNT(cd.id) AS wrongNum FROM database_info a, correct_content_detail cd, database_tree_info dti WHERE cd.site_code = dti.site_code and a.site_code = dti.site_code
and dti.org_site_code= #{orgSiteCode}
and dti.is_link = #{isLink}
and dti.layer_type = #{siteType}
and cd.correct_type = #{correctType}
AND (cd.scan_date between #{startDate} and #{endDate})
and a.isexp = #{isExp}
and cd.scan_date=#{scanDate}
AND cd.exposure = 1 AND cd.del_flag = 0 GROUP BY cd.site_code ORDER BY wrongNum DESC ) AS aa
