mapper.update:
update crm_products
id = #{id}, order_id = #{orderId}, order_code = #{orderCode}, product_id = #{productId}, product_code = #{productCode}, product_name = #{productName}, product_pid = #{productPid}, product_pname = #{productPname}, product_type = #{productType}, check_count = #{checkCount}, remaindercount = #{remaindercount}, monitor_count = #{monitorCount}, is_correct = #{isCorrect}, execute_status = #{executeStatus}, is_search_tb = #{isSearchTb}, create_time = #{createTime}, modify_time = #{modifyTime},
where id=#{id}

mapper.select:
select * from crm_products


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

		SELECT
			cp.*, co.site_code siteCode,
			co.order_begin_time beginTime,
			co.order_end_time endTime
		FROM
			crm_products cp,
			crm_order co
		WHERE
			cp.order_id = co.id

			AND cp.is_search_tb = #{isSearchTb}


			AND cp.execute_status = #{executeStatus}


			AND cp.execute_status != #{notExecuteStatus}


			AND cp.execute_status in

      				#{term}



			AND cp.product_type = #{productType}


			AND cp.product_type in

      				#{term}



			AND co.site_code = #{siteCode}


			and co.site_code in

					#{item.orgSiteCode}



			AND length(co.site_code)=6


			AND length(co.site_code)=10


				and #{nowTime} BETWEEN co.order_begin_time AND co.order_end_time


			order by

				${queryOrder}



			GROUP BY co.site_code

		 SELECT
			cp.*
		FROM
			database_tree_info d,
			database_tree_info dd,
			crm_products cp,
			crm_order co
		WHERE
			d.parent_id = dd.id
		AND d.is_link = 1
		AND dd.is_link = 1

			AND d.site_code = #{siteCode}

		AND d.site_code = co.site_code
		AND cp.order_id = co.id

			AND cp.product_type = #{productType}


			AND cp.execute_status = #{executeStatus}


			AND co.order_types = #{orderTypes}



			AND cp.execute_status in

      				#{term}



			AND cp.is_search_tb = #{isSearchTb}

SELECT cp.*, co.site_code siteCode, co.order_begin_time beginTime, co.order_end_time endTime FROM crm_products cp, crm_order co, database_tree_info dti WHERE cp.order_id = co.id AND co.site_code = dti.org_site_code
AND dti.site_code = #{siteCode}
AND dti.is_link = #{isLink}
AND dti.isexp = #{isExp}
and #{nowTime} BETWEEN co.order_begin_time AND co.order_end_time

		SELECT
			cp.* 
		FROM
