			AND cp.execute_status = #{executeStatus}


			AND cp.execute_status != #{notExecuteStatus}


			AND cp.execute_status in

      				#{term}



			AND co.site_code = #{siteCode}


			AND co.proto_contract_code != ""


				and #{nowTime} BETWEEN co.order_begin_time AND co.order_end_time


			order by

				${queryOrder}

SELECT 
  cp.ID
FROM
  database_tree_info dti,
  crm_order co,
  crm_products cp 
WHERE dti.org_site_code = co.site_code 
  AND co.id = cp.order_id 

  	AND cp.execute_status = #{executeStatus}


  	AND cp.product_type = #{productType} 


  	AND cp.is_search_tb = #{isSearchTb}


   	AND dti.site_code =#{siteCode}
