SELECT ic.site_code AS siteCode, wi.name AS siteName, ic.convert_scores AS convertScores, ic.combo_id AS comboId FROM index_count ic,database_info wi WHERE ic.site_code =wi.site_code
and ic.scan_date =#{scanDate}
and ic.type=#{type}
AND ic.site_code in
#{item}
AND ic.site_code in
#{items.siteCode}
GROUP BY ic.site_code ORDER BY ic.convert_scores DESC
