id = #{id}, link_all_info_id = #{linkAllInfoId}, service_period_id = #{servicePeriodId}, site_code = #{siteCode}, url = #{url}, scan_level = #{scanLevel}, link_title = #{linkTitle}, parent_title = #{parentTitle}, url_type = #{urlType}, resource_type = #{resourceType}, scope = #{scope}, error_describe = #{errorDescribe}, error_code = #{errorCode}, img_url = #{imgUrl}, scan_time = #{scanTime}, create_time = #{createTime}, operator = #{operator}, del_flag = #{delFlag}
