sql.where:
1=1

	and site_code=#{siteCode}


	and scan_date=#{scanDate}


	and scan_date BETWEEN #{beginScanDate} AND #{endScanDate}

mapper.update:
update monitor_org_include
id = #{id}, site_code = #{siteCode}, site_sum = #{siteSum}, baidu_site_sum = #{baiduSiteSum}, qihu_site_sum = #{qihuSiteSum}, sogou_site_sum = #{sogouSiteSum}, google_site_sum = #{googleSiteSum}, baidu_domain_sum = #{baiduDomainSum}, qihu_domain_sum = #{qihuDomainSum}, sogou_domain_sum = #{sogouDomainSum}, google_domain_sum = #{googleDomainSum}, baidu_site_avg = #{baiduSiteAvg}, qihu_site_avg = #{qihuSiteAvg}, sogou_site_avg = #{sogouSiteAvg}, google_site_avg = #{googleSiteAvg}, baidu_domain_avg = #{baiduDomainAvg}, qihu_domain_avg = #{qihuDomainAvg}, sogou_domain_avg = #{sogouDomainAvg}, google_domain_avg = #{googleDomainAvg}, scan_date = #{scanDate}, create_time = #{createTime}, modify_time = #{modifyTime},
where id=#{id}

mapper.select:
select * from monitor_org_include


	order by

