mapper.update:
update notice_receiver
id = #{id}, site_code = #{siteCode}, notice_sender_id = #{noticeSenderId}, feed_contents = #{feedContents}, feed_affix_url = #{feedAffixUrl}, feed_affix_name = #{feedAffixName}, status = #{status}, feed_date = #{feedDate}, is_read = #{isRead}, create_time = #{createTime}, return_time = #{returnTime}, modify_time=now()
where id=#{id}

mapper.select:
select * from notice_receiver


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

select n.*,di.name as siteName 
from notice_receiver n,database_info di
where n.site_code = di.site_code

   and n.notice_sender_id=#{noticeSenderId}


   and n.status=#{status}


  and (di.site_code like '%' #{siteSearch} '%' or di.name like '%' #{siteSearch} '%' )


	and n.feed_date>=#{feedDateStart}


	and n.feed_date<=#{feedDateEnd}

order by n.status=2 desc,n.modify_time desc

select s.topic,s.contents,s.affix_url,s.affix_name,s.feed_deadline_date,r.*
from notice_receiver r,notice_sender s
where r.notice_sender_id = s.id

	and r.site_code=#{siteCode}


  and s.topic like '%' #{topic} '%' 



	and r.feed_date>=#{feedDateStart}


	and r.feed_date<=#{feedDateEnd}

order by r.modify_time desc
