sql.where:
1=1

	 and #{forwardDate} <= start_date    


	 and  start_date <= #{startDate}  


	and #{nowTime} BETWEEN start_date and end_date


	and #{yesterDay} BETWEEN start_date and end_date


	and combo_id =#{comboId}


	and site_code =#{siteCode}


	and service_period_id =#{servicePeriodId}


	and is_scan =#{isScan}


	and website_task_num  not like '${notWebsiteTaskNum}%'

mapper.update:
update relations_period
Id = #{Id}, site_code = #{siteCode}, service_period_id = #{servicePeriodId}, start_date = #{startDate}, end_date = #{endDate}, combo_id = #{comboId},
where id=#{id}

mapper.select:
select * from relations_period


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

SELECT ci.site_code AS siteCode FROM relations_period rp, service_period sp, contract_info ci WHERE rp.service_period_id = sp.id AND sp.contract_info_id = ci.id
and rp.site_code=#{siteCode}
and #{newTime} BETWEEN rp.start_date AND rp.end_date

SELECT 0 columnNum, di.site_code, di.`name`, di.url homePageUrl, di.jump_url jumpPageUrl, di.isorganizational FROM relations_period rp, database_info di, database_tree_info dti WHERE rp.site_code = di.site_code AND rp.site_code = dti.site_code
and rp.service_period_id=#{servicePeriodId}
and dti.org_site_code= #{orgSiteCode}
and dti.is_link = #{isLink}
and dti.layer_type = #{siteType}
