sql.where:
1=1

	and service_period_id=#{servicePeriodId}


	and site_code=#{siteCode}


	and  (site_code like '%${term}%'  OR  
		 site_name like '%${term}%') 


	and create_time like '${createTime}%'


	and send_wx_state =#{sendWxState}


	and site_code in 

		#{item.siteCode}

mapper.update:
update report_manage_log
id = #{id}, service_period_id = #{servicePeriodId}, site_code = #{siteCode}, site_name = #{siteName}, send_time = #{sendTime}, send_state = #{sendState}, send_wx_state=#{sendWxState}, create_time = #{createTime}
where id=#{id}

mapper.select:
select * from report_manage_log


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

SELECT a.site_code AS siteCode, a.site_name AS siteName, a.send_time AS sendTime, a.send_state AS sendState, c.dic_site_id AS dicSiteId, c.dic_combo_id AS dicComboId, c.service_begin_time as serviceBeginTime, d.pdf_url AS pdfUrl, b.start_time AS startTime, b.period_num AS periodNum FROM report_manage_log a, service_period b, database_info c, report_word_result d WHERE a.site_code=b.site_code AND a.site_code=c.site_code AND a.service_period_id=b.id AND a.site_code = d.site_code AND d.service_period_id=b.id
AND a.site_code IN
#{websiteIdList.siteCode}
AND b.website_info_id IN
#{websiteIdList.id}
and a.site_code in
#{item}
AND b.period_num=#{periodNum}
AND c.dic_combo_id=#{comboType}
and #{nowTime} BETWEEN c.service_begin_time AND c.service_end_time
order by a.send_time desc
