mapper.update:
update report_word_result 

		id = #{id},
		service_period_id = #{servicePeriodId},
		site_code = #{siteCode},
		type = #{type},
		word_url = #{wordUrl}
		pdf_url = #{pdfUrl}
		excel_url = #{excelUrl}
		alias_name = #{aliasName}
		create_time = #{createTime}

	where id=#{id}

mapper.select:
select * from report_word_result


	group by ${groupBy}


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

SELECT rwr.* FROM report_word_result rwr, service_period sp, database_info wi WHERE rwr.site_code = sp.site_code AND rwr.service_period_id = sp.id AND wi.site_code = rwr.site_code
AND rwr.site_code IN
#{item}
AND sp.period_num = #{periodNum}
AND #{nowTime} BETWEEN wi.service_begin_time AND wi.service_end_time

	select 
		distinct (rwr.id),rwr.*,scn.check_report_result,sp.start_date,sp.end_date,
		scn.type as scnType,
		scn.id as scnId,
		scn.is_read,
		scn.director
	from 
		report_word_result rwr, 
		spot_check_notice scn, 
		service_period sp ,
		database_tree_info trin 
	where 
		rwr.site_code=scn.site_code 
		AND scn.site_code = trin.site_code
		AND trin.is_link = 1

  		AND trin.layer_type = #{layerType} 


  		AND trin.code like concat(#{code},'%')


  		AND trin.level = ${level} 

				and scn.service_period_id=rwr.service_period_id 
		and rwr.service_period_id=sp.id 
		and sp.status!=-1 and scn.check_report_result is not null 

  		AND rwr.site_code = #{siteCode} 


  		AND scn.org_site_code = #{orgSiteCode} 


  		AND scn.upper_org_code = #{upperOrgCode} 


  		AND rwr.alias_name like CONCAT('%',#{aliasName},'%') 


  		AND scn.check_report_result  = #{checkReportResult} 


  		AND scn.is_read  = #{isRead} 



  			AND scn.org_site_code IN 

		  		#{item}





		order by rwr.

			${queryOrder}



		limit #{start}, #{pageSize}

select count(t.disId) from (
	select 
		distinct(rwr.id) disId
	from 
		report_word_result rwr, 
		spot_check_notice scn, 
		service_period sp ,
		database_tree_info trin 
	where 
		rwr.site_code=scn.site_code 
		AND scn.site_code = trin.site_code
		AND trin.is_link = 1

  		AND trin.layer_type = #{layerType} 

		and scn.service_period_id=rwr.service_period_id 
		and rwr.service_period_id=sp.id 
		and sp.status!=-1 and scn.check_report_result is not null 

  		AND rwr.site_code = #{siteCode} 


  		AND trin.code like concat(#{code},'%')


  		AND trin.level = ${level} 


  		AND scn.org_site_code = #{orgSiteCode} 


  		AND scn.upper_org_code = #{upperOrgCode} 


  		AND rwr.alias_name like CONCAT('%',#{aliasName},'%') 


  		AND scn.check_report_result  = #{checkReportResult} 


  		AND scn.is_read  = #{isRead} 



  			AND scn.org_site_code IN 

		  		#{item}




		order by rwr.

			${queryOrder}


	) t
