mapper.update:
update security_fatal_error
id = #{id}, site_code = #{siteCode}, service_period_id = #{servicePeriodId}, name = #{name}, url = #{url}, type = #{type}, problem_desc = #{problemDesc}, img_url = #{imgUrl}, user_id = #{userId}, scan_date = #{scanDate}, modify_time = #{modifyTime}, del_flag = #{delFlag}
where id=#{id}

mapper.select:
select * from security_fatal_error


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

		SELECT 
		  sr.service_period_id AS serviceperiodId,
		  sr.site_code AS siteCode,
		  di.name AS siteName,
		  COUNT(sr.id) AS fatalErrorNum 
		FROM
		  security_fatal_error sr 
		  LEFT JOIN database_tree_info dti 
		    ON sr.site_code = dti.site_code 
		  LEFT JOIN database_info di 
		    ON dti.site_code = di.site_code 
		WHERE 1 = 1

	    	AND sr.service_period_id=#{servicePeroidId}


	    	AND dti.org_site_code=#{orgSiteCode}


	    	AND dti.is_link=#{isLink}


	    	AND dti.layer_type=#{siteType}


	    	AND  di.isexp=#{isExp}


    		AND sr.scan_date <=#{scanDate}


	   		AND sr.del_flag=#{delFlag}

	   GROUP BY sr.site_code
	   ORDER BY fatalErrorNum DESC

			limit #{begin},#{end}

	SELECT COUNT(*) FROM (
		SELECT 
			  sr.service_period_id AS serviceperiodId,
			  sr.site_code AS siteCode,
			  di.name AS siteName,
			  COUNT(sr.id) AS fatalErrorNum 
			FROM
			  security_fatal_error sr 
			  LEFT JOIN database_tree_info dti 
			    ON sr.site_code = dti.site_code 
			  LEFT JOIN database_info di 
			    ON dti.site_code = di.site_code 
			WHERE 1 = 1

		    	AND sr.service_period_id=#{servicePeroidId}


		    	AND dti.org_site_code=#{orgSiteCode}


		    	AND dti.is_link=#{isLink}


		    	AND dti.layer_type=#{siteType}


		    	AND  di.isexp=#{isExp}


    			AND sr.scan_date <=#{scanDate}


		   		AND sr.del_flag=#{delFlag}

		   GROUP BY sr.site_code
	) AS a
