mapper.update:
update service_period
id = #{id}, start_date = #{startDate}, end_date = #{endDate}, create_time = #{createTime}, modify_time = #{modifyTime}, combo_id = #{comboId}, contract_info_id = #{contractInfoId}, spot_check_schedule_id = #{spotCheckScheduleId}, web_count = #{webCount}, status = #{status},
where id=#{id}

mapper.select:
select * from service_period


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

select distinct s.id as id,s.start_date as startDate,s.end_date as endDate from service_period s join relations_period r 
on s.id = r.service_period_id 


order by

	${queryOrder}



limit #{start}, #{pageSize}

SELECT 
  sp.* 
FROM
  contract_info ci 
  LEFT JOIN service_period sp 
    ON ci.id = sp.contract_info_id 
WHERE 1=1

	AND ci.site_code=#{siteCode}


	AND ci.execute_status=#{executeStatus}


	AND (ci.contract_code_spot='' or ci.contract_code_spot is null)


	AND sp.combo_id=#{comboId}


	AND sp.status=#{status}


	AND ci.site_code in

			#{item.siteCode}


ORDER BY ci.create_time,sp.create_time DESC
