sql.where:
1=1

    and site_code = #{siteCode}


    and contract_info_id = #{contractInfoId}


	and task_name like '%${taskName}%'


	and create_time  BETWEEN #{beginTime}  AND #{endTime}



	and batch_num= #{batchNum}


	and group_num= #{groupNum}


	and status= #{status}


	and report_org_code= #{reportOrgCode}

mapper.update:
update spot_check_schedule
id = #{id}, site_code = #{siteCode}, contract_info_id = #{contractInfoId}, batch_num = #{batchNum}, group_num = #{groupNum}, task_name = #{taskName}, start_date = #{startDate}, end_date = #{endDate}, status = #{status}, spot_website_num = #{spotWebsiteNum}, spot_level = #{spotLevel}, isorganizational = #{isorganizational}, create_time = #{createTime}, modify_time = #{modifyTime}, report_org_code = #{reportOrgCode},
where id=#{id}

mapper.select:
select * from spot_check_schedule


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

SELECT * FROM ( SELECT sc.id id, sc.site_code site_code, sc.contract_info_id, sc.batch_num batch_num, sc.group_num group_num, sc.task_name task_name, sc.start_date start_date, sc.end_date end_date, sc.status STATUS, sc.spot_website_num spot_website_num, sc.report_org_code report_org_code, sc.modify_time modify_time, re.affix_url affix_url, re.affix_name affix_name, re.type TYPE, re.id upId FROM spot_check_schedule sc JOIN spot_security_report re ON sc.id = re.spot_check_schedule_id WHERE re.type='1'
and sc.report_org_code= #{reportOrgCode}
UNION ALL SELECT rt.spot_check_schedule_id id, rt.site_code site_code, NULL AS contract_info_id, NULL AS batch_num, NULL AS group_num, rt.task_name task_name, rt.start_date start_date, rt.end_date end_date, rt.status STATUS, rt.spot_website_num spot_website_num, rt.report_org_code report_org_code, rt.modify_time modify_time, rt.affix_url affix_url, rt.affix_name affix_name, rt.type TYPE, rt.id upId FROM spot_security_report rt WHERE rt.type="2"
and rt.report_org_code= #{reportOrgCode}
) t WHERE 1=1
and t.task_name like CONCAT('%',#{taskName},'%') OR t.site_code like CONCAT(#{taskName},'%')
ORDER BY t.modify_time DESC
