mapper.update:
update update_channel_info
id = #{id}, dic_channel_id = #{dicChannelId}, site_code = #{siteCode}, update_num = #{updateNum}, scan_date = #{scanDate}, create_time = #{createTime},
where id=#{id}

mapper.select:
select * from update_channel_info


	order by

		${queryOrder}



	limit #{start}, #{pageSize}

		select  a.site_code as siteCode,sum(a.update_num) as updateNum,b.site_name as siteName,b.home_page_url as homePageUrl,b.jump_page_url as jumpPageUrl from update_channel_info a,database_info b 
		where a.site_code =b.site_code 

		and a.scan_date=#{scanDate}


			 	and a.site_code in

       					#{term.siteCode}


        group by a.site_code

		select  a.site_code as siteCode,sum(a.update_num) as updateNum,b.site_name as siteName,b.home_page_url as homePageUrl,b.jump_page_url as jumpPageUrl from update_channel_info a,database_info b 
