SELECT site_code siteCode, dic_channel_id dicChannelId, SUM(update_num) AS updateNum FROM update_channel_info WHERE 1 = 1
and scan_date BETWEEN #{beginScanDate} AND #{endScanDate}
AND site_code like '${siteCode}%'
and site_code in
#{term.siteCode}
and dic_channel_id=#{dicChannelId}
and dic_channel_id in
#{term}
GROUP BY site_code, dicChannelId
